using AdventOfCode.Day2;
using NUnit.Framework;

namespace AdventOfCode.Tests.Day2
{
    [TestFixture]
    public class CodeParserTests
    {
        private BathroomCodeParser codeParser;

        [SetUp]
        public void Setup()
        {
            codeParser = new BathroomCodeParser();
        }

        [TestCase(@"ULL
RRDDD
LURDL
UUUUD", 1985)]
        public void VerifyBathroomCode(string input, int code)
        {
            var bathroomCode = codeParser.ParseBathroomCode(input);
            Assert.That(bathroomCode, Is.EqualTo(code));
        }

        [Test]
        public void GetBathroomCode()
        {
            var input = @"



";

            var bathroomCode = codeParser.ParseBathroomCode(input);
            Assert.Pass($"Bathroom code is {bathroomCode}");
        }

        [TestCase(@"ULL
RRDDD
LURDL
UUUUD", "5DB3")]
        public void VerifyFancyBathroomCode(string input, string code)
        {
            var bathroomCode = codeParser.ParseFancyBathroomCode(input);
            Assert.That(bathroomCode, Is.EqualTo(code));
        }

        [Test]
        public void GetFancyBathroomCode()
        {
            var input = @"



";

            var bathroomCode = codeParser.ParseFancyBathroomCode(input);
            Assert.Pass($"Fancy bathroom code is {bathroomCode}");
        }
    }
}
